import java.util.Arrays;

            class Test {
              public static void main(String[] args) {
                Object[] a = {1, 2};
                Object b = "hello";
                f(0, a);
                f(0, b);
                for (boolean flag : new boolean[] {true, false}) {
                  f(0, 1, flag ? a : b);
                }
              }

              static void f(int x, Object... xs) {
                System.err.println(Arrays.deepToString(xs));
              }
            }
