        if (query.length() < MIN_QUERY_LENGTH) {
            return Stream.empty();
        }

        // violation here
        Stream<MatchResult<T>> base = candidates.stream()
                                                .map(it -> {
                                                    String cand = matchExtractor.apply(it);
